def verify_keys(a,b):
	while True:
		if a%4==3:
			while b%4!=3:
				b=nextprime(b)
			return a,b
		if b%4==3:
			while a%4!=3:
				a=nextprime(a)
			return a,b
		a,b=nextprime(a),nextprime(b)

def dec(msg, bs, ll, mds):
	x = 15564066497424
	c = ''
	for i in range(ll):
		x = pow(x,2,mds)
		p = (bin(x)[2:])[-bs:] #ekher block
		c_i = int(p,2)^int(msg[i*bs:(i+1)*bs],2)
		ci_bin = format(c_i, '0' + str(bs) + 'b') #bin
		c += ci_bin
	return c

